import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faX } from "@fortawesome/free-solid-svg-icons";
import { useDispatch, useSelector } from "react-redux";
import { toggleDrawer } from "../reducers/mobileNavSlice";
import { useNavigate } from "react-router-dom";
import { toggleSignin, toggleSignup } from "../reducers/modalSigninSignup";

const MobileNav = () => {
    const dispath = useDispatch();
    const toggleDrawer = useSelector((state) => state.mobileNav.toggleDrawer);

    let drawerClasses = toggleDrawer ? "side-drawer open" : "side-drawer";

    const navigate = useNavigate();

    return (
    <div className={drawerClasses}>
        <div className="drawer-container">
            <div className="drawer-icon-closed">
                <FontAwesomeIcon icon={faX} style={{cursor: "pointer"}} 
                    onClick={()=> dispath(toggleDrawer())}
                />
            </div>
            <div className="drawer-item">
                <p 
                    onClick={() => {
                        navigate("/");
                        dispath(toggleDrawer());
                }}>
                    Trang chủ
                </p>
                <p 
                    onClick={() => {
                        navigate("/movies");
                        dispath(toggleDrawer())
                    }}
                >
                    Phim chiếu
                </p>
                <p 
                    onClick={() => {
                        navigate("/news");
                        dispath(toggleDrawer());
                    }}
                >
                    Tin tức
                </p>
                <div className="drawer-signup-signin">
                    <p onClick={()=> {
                        dispath(toggleSignin());
                        dispath(toggleDrawer());
                    }}>Đăng Nhập</p>
                    <p onClick={()=> {
                        dispath(toggleSignup());
                        dispath(toggleDrawer());
                    }}>Đăng ký</p>
                </div>
            </div>
        </div>
    </div>
    );
};

export default MobileNav;
