import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    showDrawer: false,
}

const mobileNavSlide = createSlice({
    name: "mobileNav",
    initialState,
    reducers: {
        toggleDrawer(state) {
            state.showDrawer = !state.showDrawer;
        },
    },
});

export const { toggleDrawer } = mobileNavSlide.actions;

export default mobileNavSlide.reducer;
